//--------------------------------------------------------------
void ofApp::draw(){
    if(testImage.isAllocated()) {
        testImage.draw(0,0);
    }
    
    if(testImage2.isAllocated()) {
        testImage2.draw(0,testImage.getHeight() + 5);
    }
}

